mapper.update:
update campus_delivery.coupon
name = #{name}, type = #{type}, amount = #{amount}, min_point = #{minPoint}, total_count = #{totalCount}, remaining_count = #{remainingCount}, status = #{status}, start_time = #{startTime}, end_time = #{endTime}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

mapper.select:
select * from campus_delivery.coupon

    name like concat('%', #{name}, '%')
    and type = #{type}
    and status = #{status}
    and start_time >= #{startTime}
    and end_time <= #{endTime}
