mapper.select:
select d.*, c.name as categoryName
from campus_delivery.dish d left join campus_delivery.category c on d.category_id = c.id


        d.name like concat('%',#{name},'%')


        and d.category_id = #{categoryId}


        and d.status = #{status}


order by create_time desc

mapper.update:
update campus_delivery.dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}
where id = #{id}

mapper.select:
select * from campus_delivery.dish


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}


order by create_time desc

select count(id) from campus_delivery.dish


        and status = #{status}


        and category_id = #{categoryId}
