mapper.select:
select * from campus_delivery.orders


        and user_id = #{userId}



        and status = #{status}


        and number like concat('%',#{number},'%')


        and phone like concat('%',#{phone},'%')


        and order_time  >= #{beginTime}

select sum(amount) from campus_delivery.orders


        and order_time > #{begin}


        and order_time < #{end}


        and status = #{status}

select count(id) from campus_delivery.orders


        and order_time > #{begin}


        and order_time < #{end}


        and status = #{status}

select sum(od.number) as number, od.name as name from campus_delivery.order_detail od, campus_delivery.orders ods
ods.id = od.order_id and ods.status = 6 and ods.order_time > #{beginTime} and ods.order_time < #{endTime}
group by od.name order by number desc, od.name desc limit 0,10
